    private void jButton_CalculateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton_CalculateActionPerformed
        jLabel_up.setVisible(false);
        jLabel_down.setVisible(false);

        if (jTextField_KG.getText().equals("")) {
            jLabel_up.setVisible(true);
            jLabel_warning.setText("Enter the value in space");
            jLabel_warning.setVisible(true);

        }
        if (jTextField_mtr.getText().equals("")) {
            jLabel_down.setVisible(true);
            jLabel_warning.setText("Enter the value in space");
            jLabel_warning.setVisible(true);
        } else {
            jLabel_warning.setText("");
            try{
            double kg = Double.parseDouble(jTextField_KG.getText());
            double ht = Double.parseDouble(jTextField_mtr.getText());
            double ans =  Math.round( kg/(ht*ht) * 100.0) / 100.0 ;
            if(ans <18.5)
            jLabel2.setText("Underweight");
            else if(ans > 18.5 && ans < 24.90)
            jLabel2.setText("Healthy weight");
            else if(ans > 25 && ans < 29.9)
            jLabel2.setText("Overweight");
            else
            jLabel2.setText("Obese");
            jLabel_Result.setText(String.valueOf(ans));
            }catch(Exception ex){
                JFrame f= new JFrame();
                JOptionPane.showMessageDialog(f, "Check Input in the field!", "Oopss..", JOptionPane.WARNING_MESSAGE);
            }
        }
    }//GEN-LAST:event_jButton_CalculateActionPerformed

    private void jTextField_mtrKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_jTextField_mtrKeyTyped
        if(!(Character.isDigit(evt.getKeyChar()) || evt.getKeyChar()== '.')){
            jLabel_warning.setText("Enter Only Digit");
            jLabel_warning.setVisible(true);
            evt.consume();
        }else{
            jLabel_warning.setText("");
        }
    }//GEN-LAST:event_jTextField_mtrKeyTyped

